import { useState } from 'react';
import '../node_modules/bootstrap/dist/css/bootstrap.min.css';

function App() {

  const fee_structure={
    "Exam Fee":{
        "INDIAN":{
            "ALL_COURSES":{
                "ALL_LEVEL":{
                    "amount":400
                }
            }
        },
        "FOREIGN":{
            "ALL_COURSES":{
                "ALL_LEVEL":{
                    "amount":100
                }
            }
        },
        "NRI":{
            "ALL_COURSES":{
                "ALL_LEVEL":{
                    "amount":600
                }
            }
        },
        "SAARC":{
            "ALL_COURSES":{
                "ALL_LEVEL":{
                    "amount":600
                }
            }
        }
    },
    "Application Fee":{
        "INDIAN":{
            "ALL_COURSES":{
                "UG":{
                    "amount":200
                },
                "UG-DIPLOMA":{
                    "amount":300
                },
                "PG":{
                    "amount":500
                }
            }
        },
        "FOREIGN":{
            "ALL_COURSES":{
                "UG":{
                    "amount":400
                },
                "UG-DIPLOMA":{
                    "amount":400
                },
                "PG":{
                    "amount":700
                }
            }
        }
    }
}
const [form, setForm] = useState({
  feeType:'',
  nationality:'',
  courses:'',
  levels:'',



})    

const {feeType, nationality, courses, levels}= form;

  return (
    <div className="container">
    <form>
    <div className="form-group">
    <label >Select the Type of Fee</label>
    <select name="feeType" value={feeType} className="form-control" id="exampleFormControlSelect1" onChange={e=>setForm({...form, [e.target.name]:e.target.value})} >

  {
    Object.keys(fee_structure).map((item)=>(<option>{item}</option>))
  }
    </select>




    </div>

{feeType?( <div className="form-group">
    <label >Select Your Nationality</label>
    <select name="nationality" value={nationality} className="form-control" id="exampleFormControlSelect1" onChange={e=>setForm({...form, [e.target.name]:e.target.value})} >

  {
    Object.keys(fee_structure[feeType]).map((item)=>(<option>{item}</option>))
  }
    </select>




    </div>):(<p>Please select Fee type</p>)}


    <div className="form-group">
    <label >Select course</label>
    <select name="courses" value={courses} className="form-control" id="exampleFormControlSelect1" onChange={e=>setForm({...form, [e.target.name]:e.target.value})} >

  <option>Medical</option>
  <option></option>
    </select>




    </div>

    <div className="form-group">
    <label >Select Level</label>
    <select name="levels" value={levels} className="form-control" id="exampleFormControlSelect1" onChange={e=>setForm({...form, [e.target.name]:e.target.value})} >

  {
    Object.keys(fee_structure).map((item)=>(<option>{item}</option>))
  }
    </select>




    </div>
    </form>
    </div>
    );
  }

  export default App;
